import React, { useState } from 'react';

function ModeToggle() {
    const [isBlack, setIsBlack] = useState(false);

    const toggleColor = () => {
      setIsBlack(!isBlack);
    };

    return (
      <div className={isBlack ? "container black" : "container white"}>
        <button onClick={toggleColor}>Toggle Color</button>
      </div>
    );
}

export default ModeToggle;
